definition.script:
// sAccount: The slurm account
def sAccount = 'imp'
def designName = 'coreip_e3_min_ppa_28nm_9t'
def confBuildDir 
def jid
def des
def dirExist
def buildLogName
def codeName = '1004'

pipeline {
    agent {label 'tiny_rhel8'}
    stages {
        stage('Request Slurm Resource') {
            steps {
                script {
                    // confBuildDir: this build will run in this folder
                    confBuildDir = "/scratch/jenkins/archived-builds/$JOB_NAME/$BUILD_ID"
                    // jid: the jod ID that is allocated by slurm
                    jid = sh (
                        script: "salloc --account=${sAccount} --job-name=$JOB_NAME --time=5:0:0 -c 4 --mem=16G -D /tmp --no-shell 2>&1|grep Granted|grep -oE '[0-9]+'",
                        returnStdout: true).trim()
                    // des: the server that is allocated by slurm
                    des = sh (
                        script: "scontrol show job ${jid}|grep '[^a-zA-Z]NodeList=' |awk -F'=' '{print \$2}'",
                        returnStdout: true).trim()
                    // dirExist: check if the build folder is made successfully
                    dirExist = sh (
                        script: "srun --account=${sAccount} --jobid=${jid} -D /tmp mkdir -p ${confBuildDir}",
                        returnStatus: true) == 0

                    sh "hostname"
                }
            }
        }
        stage ('build') {
            steps {
                script {
                    echo "got host: $des"
                    sh """
                       srun_cmd="srun --account=${sAccount} --export=ALL --jobid=${jid} -D ${confBuildDir}"
                        \$srun_cmd hostname
                        \$srun_cmd git clone --dissociate git@github.com:sifive/federation.git federation_${codeName}
                        \$srun_cmd ls -rtl
                        \$srun_cmd pwd
                        \${srun_cmd}/federation_${codeName}   ./scripts/quick-submodule-update-with-ppa-tools
                        \${srun_cmd}/federation_${codeName} ls -rtl
                        \${srun_cmd}/federation_${codeName} --export=ALL,BUILD_DIR=builds ./generate-build ${designName}
                        \${srun_cmd}/federation_${codeName}/builds/${designName} scontrol show config | grep InactiveLimit
                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make firrtl
                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make imp-setup
                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make syn
                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make pnr
                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make sta
                    """
                }
            }
        }
    }
//                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make imp-setup
//                        \${srun_cmd}/federation_${codeName}/builds/${designName} -t 5:0:0 make syn
    post {
        always {
            script {
                if (jid) {
                    sh "echo pass"
                }
            } //script
        } //always
    } //post
}
