definition.script:
pipeline {
    agent { label 'tiny' }

    parameters {
        choice(name: 'FUSDK_MACHINE_FILTER', choices: ['all', 'qemuriscv64', 'sifive-fpga'], description: 'To-be-test FUSDK machines')
        booleanParam(name: 'SYNC_ONLY', defaultValue: false, description: 'Sync Jenkins Job configuration only, build nothing')
    }

    stages {
        stage('Check Parameter') {
            steps {
                echo "1st: ${params.SYNC_ONLY}" // ok
                //echo '2nd: ${params.SYNC_ONLY}' illegal
                echo "3rd: ${SYNC_ONLY}" // ok
                echo '4th: ${SYNC_ONLY}' // string ${SYNC_ONLY}
                sh "echo 5th ${SYNC_ONLY}" //ok
                sh 'echo 6th ${SYNC_ONLY}' //ok
            }
        }
        stage('Hello') {
            steps {
                echo 'Hello World'
                sh 'pip3 install pytest'
            }
        }
        stage('Test') {
            steps {
                sh '''
                cat > test.xml << EOF
<?xml version="1.0" encoding="utf-8"?><testsuites><testsuite name="pytest" errors="0" failures="1" skipped="0" tests="2" time="0.044" timestamp="2021-12-22T14:38:18.280108" hostname="LewisMBA.local"><testcase classname="ch1.test_one" name="test_passing" time="0.001" /><testcase classname="ch1.test_two" name="test_failing" time="0.001"><failure message="assert (1, 2, 3) == (3, 2, 1)&#10;  At index 0 diff: 1 != 3&#10;  Use -v to get the full diff">def test_failing():
&gt;       assert (1, 2, 3) == (3, 2, 1)
E       assert (1, 2, 3) == (3, 2, 1)
E         At index 0 diff: 1 != 3
E         Use -v to get the full diff

test_two.py:2: AssertionError</failure></testcase></testsuite></testsuites>
EOF
'''

            }
        }
        stage('Post') {
            steps {
                    junit 'test.xml'
            }
        }
    }

}
