definition.script:
pipeline {
    agent { label "tiny_rhel8" }

    stages {
        stage('check customize or use default bitstream'){
            steps{
                script{
                    if(env.bitstream_custom_json == ""){
                        echo 'executing default bitstreams'
                        def bitstream_custom_json = "sdf pwpw"
                        echo "${bitstream_custom_json}"
                        echo "env.bitstream_custom_json"
                    }
                    else{
                        echo 'executing customize bitstreams'
                    }
                }
            }
        }
        stage('dispatching') {
            steps{
                script{
                    sh 'printenv'
                    def branches = [:]
                    if(env.bitstream_custom_json == ""){
                        echo 'executing default bitstreams'
                    }
                    else{
                        echo 'executing customize bitstreams'
                        def jsonString = env.bitstream_custom_json
                        def ll =  readJSON text: jsonString
                        println ll

                        for(i = 0; i < ll.size(); i += 1) {
                            def release = ll[i]["release"]
                            def core = ll[i]["core"]
                            branches["Testing on ${release}:${core}"] = {
                                build job: 'syssw-automation/Automation_Works_Folder/Jobs/deprecated/Simple_echo', parameters: [string(name: 'ECHO_STUFF', value: core)], quietPeriod: 2
                            }
                        }
                        parallel branches
                    }

                }
            }
        }
    }

}
