definition.script:
pipeline {
	agent {
		node {
			label "tiny_rhel8"
			customWorkspace "/scratch/jenkins/workspace/${JOB_NAME}/${BUILD_ID}"
		}
	}
	stages {
		stage('build') {
			when {
				expression { params.QEMU_SHA1 != "" }
			}
			steps {
				script {
					sh """
					    if [ -d "/work/build-qemu/\${QEMU_SHA1}" ]; then
					        exit 0
				        fi
					    source /sifive/tools/Modules/init-chooser
				        module load ninja/ninja/1.10.1
						git clone git@github.com:sifive/qemu-private.git
						cd qemu-private
						git reset --hard \${QEMU_SHA1}
						mkdir build
						cd build
						../configure --prefix=/work/build-qemu/\${QEMU_SHA1} --target-list=riscv64-linux-user
						ninja install
						if [ -d "/work/build-qemu/\${QEMU_SHA1}" ]; then
						    cd /work/build-qemu
						    tar zcvf \${QEMU_SHA1}.tar.gz \${QEMU_SHA1}
						fi
					"""
				}
			}
		}
	}
	post {
		cleanup {
			deleteDir()
		}
	}
}
