definition.script:
pipeline {
    agent {
		node {
			label "tiny || tiny_rhel8"
			customWorkspace "/scratch/jenkins/workspace/${JOB_NAME}/${BUILD_ID}"
		}
	}
    stages {
        stage('Trigger Builds In Parallel') {
            when {
            	expression { params.COMPILER_TYPE == "llvm" && params.OS == "linux" }
            }
            steps {
            	parallel(
            		ComputeLibrary: {
            			build job: '/ComputeLibrary/rvv', parameters: [
            				[$class: 'StringParameterValue', name: 'TOOLCHAIN_PATH', value: String.valueOf(TOOLCHAIN_PATH)]
            			]
            		},
            		eigen: {
            			build job: '/eigen/neon2rvv', parameters: [
            				[$class: 'StringParameterValue', name: 'TOOLCHAIN_PATH', value: String.valueOf(TOOLCHAIN_PATH)]
            			]
            		},
            		tensorflow: {
            			build job: '/tensorflow/rvv', parameters: [
            				[$class: 'StringParameterValue', name: 'TOOLCHAIN_PATH', value: String.valueOf(TOOLCHAIN_PATH)]
            			]
            		},
            		failFast: false
            	)
            }
        }
    }
}
