definition.script:
stage('Trigger Builds In Parallel') {
                    build job: 'tpp01--build-binary-packages--devtool-image', parameters: [[$class: 'StringParameterValue', name: 'REPO_NAME', value: String.valueOf(REPO_NAME)], [$class: 'StringParameterValue', name: 'REPO_BRANCH', value: String.valueOf(REPO_BRANCH)], [$class: 'StringParameterValue', name: 'EXTRA_SUFFIX', value: String.valueOf(EXTRA_SUFFIX)], [$class: 'StringParameterValue', name: 'EXTRA_OPTION', value: String.valueOf(EXTRA_OPTION)]]
                    if (params.UPLOAD_FILES == true) {
                        build job: 'tpp01--upload-binary-packages--devtool-image', parameters: [[$class: 'StringParameterValue', name: 'BUILD_SUFFIX', value: String.valueOf(BUILD_NUMBER)]]
                    }
                    if (params.WIPEOUT_FILES == true) {
                        build job: 'tpp01--wipeout-after-building--devtool-image'
                    }
}
